import React, { useEffect } from 'react';
import { Text, View, StyleSheet, TouchableOpacity, TextInput } from 'react-native';
import { colors } from '../../../global-styles';
import { Feather, MaterialCommunityIcons } from '@expo/vector-icons';
import Scanner from '../../scanner/scanner';
import { useProductContext } from '../../../contexts/product-context';


const ProductBarCodeScanner: React.FC = () => {
  const [type, setType] = React.useState<string>('')
  const [data, setData] = React.useState<string>('')

  const productContext = useProductContext();

  const onCodeScanned = (type: string, data: string) => {
    if (productContext.isScannerAlive) {
      setType(type);
      setData(data);
      setCode(data);
    }
  };

  const clearBarcodeData = () => {
    if (productContext.isScannerAlive) {
      setType('');
      setData('');
      clearProductData();
    }
  }

  const clearProductData = () => {
    if (productContext.isScannerAlive) {
      setCode('');
      setProductFounded(false);
      productContext.setProduct(undefined)
      setProductFoundedError('')
    }
  }

  const [code, setCode] = React.useState('');
  const [productFounded, setProductFounded] = React.useState<Boolean>(false);
  const [productFoundedError, setProductFoundedError] = React.useState<string>('');

  const handleInputChange = (text: string) => {
    setProductFoundedError('')
    return setCode(text);
  }

  const searchForProduct = async () => {
    const productSearched = await productContext.getProductByCode(code);
    if (productSearched) {
      productContext.setProduct(productSearched);
      setProductFounded(true);
      setProductFoundedError('');
    } else {
      setProductFounded(false);
      setProductFoundedError('Produto n√£o encontrado');
    }
  }

  useEffect(() => {
    if (code.length === 8) {
      searchForProduct()
      return;
    }
    setProductFounded(false);
  }, [code]);

  return (
    <View style={styles.container}>
      <View style={styles.scannerArea}>
        {productContext.isScannerAlive && (
          <Scanner
            onCodeScanned={onCodeScanned}
            clearBarcodeData={clearBarcodeData}
            BarCodeScannerContainerStyle={{
              height: 300,
              borderRadius: 10,
              backgroundColor: 'black',
              justifyContent: 'flex-end',
              alignItems: 'flex-end',
            }}
            BarCodeScannerReScanButtonStyle={{
              padding: 12,
              backgroundColor: 'red',
              borderRadius: 10,
            }}
          />
        )}

        {!data && (
          <View style={[styles.containerErrorMessage, {
            position: 'absolute',
            top: 5,
            left: 0,
            right: 0,
            alignItems: 'center',
            justifyContent: 'center',
          }]}>
            <Text style={[styles.errorMessage]}>
              <MaterialCommunityIcons
                name="barcode-scan" size={16} color="white" />
            </Text>
          </View>
        )}
      </View>
      <View style={styles.scannedProductInfo}>

        {productFoundedError && (
          <View style={styles.containerErrorMessage}>
            <Text style={styles.errorMessage}>
              {productFoundedError} <Feather name="alert-triangle" size={12} color="yellow" />
            </Text>
          </View>
        )}

        {productFounded && (
          <View style={[styles.descriptionContainer]}>
            <Text style={styles.title}>Descri√ß√£o</Text>
            <Text style={styles.description} numberOfLines={2} ellipsizeMode="tail">{productContext.product?.description}</Text>
            <Text style={styles.price}>{productContext.formatToCurrency(productContext.product?.price ?? 0)}</Text>
            <View style={styles.containerAdicionar}>
              <TouchableOpacity
                style={styles.buttonAdicionar}
                onPress={() => {
                  if(!productContext.product) return;
                  productContext.handleAddProductToCart(productContext.product);
                  setTimeout(() => {
                    clearBarcodeData();
                  }, 2000);
                }}
              >
                <Text style={styles.buttonAdicionarText}>Adicionar ao Carrinho</Text>
              </TouchableOpacity>
            </View>
          </View>
        )}

        {!productFoundedError && !productFounded && (
          <View>
            <Text style={[styles.title, { textDecorationLine: 'underline' }]}>ou</Text>
          </View>
        )}

        <View style={styles.containerInputCode}>
          <TextInput
            style={styles.inputInsertCode}
            placeholder='Digite o C√≥digo'
            placeholderTextColor='white'
            keyboardType='numeric'
            onChangeText={handleInputChange}
            value={code}
            maxLength={8}
          >
          </TextInput>
          <TouchableOpacity
            style={styles.buttonLimpar}
            onPress={() => {
              clearBarcodeData();
            }}
          >
            <Feather name="trash-2" size={24} color="white" />
          </TouchableOpacity>
        </View>
      </View>
    </View >
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  scannerArea: {
    backgroundColor: 'transparent',
    padding: 10,
  },
  title: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
    flexDirection: 'column',
    textAlign: 'center',
  },
  containerTitle: {
    alignItems: 'center',
  },
  reScanButtonContainer: {
    position: 'absolute',
    bottom: 0,
    right: 10,
  },
  reScanButton: {
    marginVertical: 12,
    backgroundColor: 'red',
    padding: 12,
    borderRadius: 12,
  },
  reScanButtonText: {
    color: 'white',
    fontWeight: 'bold',
  },
  scannedProductInfo: {
  },
  descriptionContainer: {
    padding: 6,
    backgroundColor: '#484848',
    borderRadius: 12,
    margin: 6,
    borderWidth: 1,
    borderColor: 'green',
  },
  text: {
    color: 'white',
  },
  description: {
    marginTop: 2,
    fontSize: 12,
    textTransform: 'uppercase',
    fontWeight: '500',
    color: 'white',
  },
  containerErrorMessage: {
    alignItems: 'center',
  },
  errorMessage: {
    marginTop: 10,
    fontSize: 12,
    textTransform: 'uppercase',
    fontWeight: '500',
    color: 'white',
    backgroundColor: 'black',
    borderColor: 'yellow',
    borderWidth: 1,
    borderRadius: 6,
    padding: 8,
    textAlign: 'center',
  },
  price: {
    fontSize: 14,
    fontWeight: '500',
    textAlign: 'right',
    color: 'lightgreen',
  },
  containerAdicionar: {
    justifyContent: 'center',
    alignItems: 'center',
    marginVertical: 4,
  },
  buttonAdicionar: {
    backgroundColor: colors.secondaryColor,
    borderRadius: 10,
    padding: 12,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 5,
  },
  buttonAdicionarText: {
    color: 'white',
    textAlign: 'center',
    fontWeight: 'bold',
    fontSize: 16,
  },
  buttonLimpar: {
    backgroundColor: 'black',
    borderRadius: 10,
    padding: 10,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 5,
  },
  containerInputCode: {
    width: '100%',
    marginVertical: 10,
    flexDirection: 'row',
    justifyContent: 'center',
  },
  inputInsertCode: {
    width: '80%',
    backgroundColor: '#7F8A7D',
    borderRadius: 10,
    padding: 10,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 5,
    color: 'white',
    marginRight: 8,
  },
});

export default ProductBarCodeScanner;
