import React, { createContext, useEffect, useState } from 'react';
import Toast from 'react-native-toast-message';
import Product from '../models/productModel';
import AsyncStorage from '@react-native-async-storage/async-storage';

interface ProductContextProps {
  isScannerAlive: boolean;
  setIsScannerAlive: React.Dispatch<React.SetStateAction<boolean>>;
  sumProducts: number;
  cartProducts: Product[];
  product: Product | undefined;
  products: Product[] | undefined;
  setProduct: React.Dispatch<React.SetStateAction<Product | undefined>>;
  setProducts: React.Dispatch<React.SetStateAction<Product[] | undefined>>;
  LoadProducts: () => Promise<Product[] | undefined>;
  getProductByCode: (code: string) => Promise<Product | undefined>;
  showToast: (type: 'success' | 'error', text1: string, text2: string) => void;
  handleAddProductToCart: (newProduct: Product) => void;
  handleRemoveFromCart: (toRemoveProduct: Product) => void;
  formatToCurrency: (value: number) => string;
}

export const ProductContext = createContext<ProductContextProps>({} as ProductContextProps);

export default function ProductProvider({ children }: { children: React.ReactNode }) {
  const [sumProducts, setSumProducts] = useState<number>(0);
  const [cartProducts, setCartProducts] = useState<Product[]>([]);
  const [isScannerAlive, setIsScannerAlive] = useState<boolean>(false);
  const [products, setProducts] = useState<Product[] | undefined>(undefined);
  const [product, setProduct] = useState<Product | undefined>(undefined);


  useEffect(() => {
    const total: number = cartProducts.reduce((accumulator, product) => accumulator + product.unit_price, 0);
    setSumProducts(Number(total.toFixed(2)));
  }, [cartProducts]);


  useEffect(() => {
    const getCartProductsInLocalStorage = async () => {
      try {
        const cartProducts = await AsyncStorage.getItem('cartProducts');
        if (cartProducts !== null) {
          setCartProducts(JSON.parse(cartProducts));
        }
      } catch (error) {
        console.error('Error fetching cartProducts from AsyncStorage:', error);
      }
    };
    getCartProductsInLocalStorage();
  }, []);

  const host: string = "http://localhost:3000";
  const BaseUrlApi: string = `${host}/product`;

  const LoadProducts = async (): Promise<Product[] | undefined> => {
    try {
      const response = await fetch("http://localhost:3000/product");
      const data = await response.json();
      console.error(data);
      setProducts(data);
      return products;
    } catch (error) {
      console.error("Error fetching products:", error);
      setProducts(undefined);
      return undefined
    }
  };

  const getProductByCode = async (code: string): Promise<Product | undefined> => {
    try {
      const response = await fetch(`${BaseUrlApi}/code/${code}`);
      const product = await response.json();
      setProduct(product);
      return product;
    } catch (error) {
      console.error("Error fetching product by code:", error);
      return undefined;
    }
  };

  const showToast = (type: 'success' | 'error', text1: string, text2: string): void => {
    Toast.show({
      type,
      text1,
      text2,
      visibilityTime: 3000,
      autoHide: true,
      text1Style: {
        fontSize: 16,
        fontWeight: 'bold',
      },
      text2Style: {
        fontSize: 14,
        fontWeight: '500',
      },
    });
  };

  const updateCartInStorage = async (products: Product[]): Promise<void> => {
    try {
      await AsyncStorage.setItem('cartProducts', JSON.stringify(products));
    } catch (error) {
      console.error('Error updating cart in AsyncStorage:', error);
    }
  };

  const handleAddProductToCart = async (newProduct: Product): Promise<void> => {
    if (cartProducts.some(product => product.code === newProduct.code)) {
      return showToast('error', 'OPS!', 'Este produto já foi adicionado ao carrinho.');
    }

    const updatedCartProducts = [newProduct, ...cartProducts];
    setCartProducts(updatedCartProducts);
    await updateCartInStorage(updatedCartProducts);
    return showToast('success', 'Sucesso!', 'Produto adicionado ao carrinho!');
  };

  const handleRemoveFromCart = async (toRemoveProduct: Product): Promise<void> => {
    const updatedCartProducts = cartProducts.filter(product => product.code !== toRemoveProduct.code);
    setCartProducts(updatedCartProducts);
    await updateCartInStorage(updatedCartProducts);
  };

  const formatToCurrency = (value: number): string => {
    return value.toLocaleString('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    })
  };

  const [isLoadingProducts, setIsLoadingProducts] = useState<boolean>(true);

  useEffect(() => {
    const getCartProductsInDB = async () => {
      try {
        const products = await LoadProducts();
        setProducts(products);
      } catch (e) {
        console.error('error ao carregar os products')
      } finally {
        setIsLoadingProducts(false);
      }
    }

    getCartProductsInDB();
  }, []);

  return (
    <ProductContext.Provider value={{
      isScannerAlive,
      sumProducts,
      cartProducts,
      product,
      setProduct,
      products,
      setProducts,
      getProductByCode,
      LoadProducts,
      setIsScannerAlive,
      showToast,
      handleAddProductToCart,
      handleRemoveFromCart,
      formatToCurrency,
    }}>
      {children}
    </ProductContext.Provider>
  );
};

export function useProductContext() {
  const context = React.useContext(ProductContext);
  return context;
}
