import * as React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { StyleSheet, Text } from 'react-native';
import type { StatusBarStyle } from 'react-native';
import { StatusBar as ExpoStatusBar } from 'expo-status-bar';
import Toast from 'react-native-toast-message'

import AuthRouter from './src/app/routes/Auth/auth-router';
import ProductProvider from './src/app/contexts/product-context';
import { useAuth } from './src/app/contexts/auth-context';
import NonAuthRouter from './src/app/routes/nonAuth/non-auth-router';
import { StripeProvider } from '@stripe/stripe-react-native';

const STRIPE_KEY = "pk_test_51OVM1qFEaryRg9iM5LewTPLyJpXOA25KdGqT2RwAw42nnkQo8oNI4QuHBz246nRHdwl9ibmpn93mrNkEcvLb1Z2t00vEsW0K1A";

function App() {
  const STYLES = ['default', 'dark-content', 'light-content'] as const;
  const [statusBarStyle, setStatusBarStyle] = React.useState<StatusBarStyle>(STYLES[2],);
  const { isLogged } = useAuth();

  return (
    <>
      <NavigationContainer>
        <ExpoStatusBar
          style={statusBarStyle === 'default' ? 'light' : 'light'}
          backgroundColor='black' />
        <StripeProvider publishableKey={STRIPE_KEY}>
          <ProductProvider>
            {/* { isLogged ? <AuthRouter /> : <NonAuthRouter /> } */}
            <AuthRouter />
          </ProductProvider>
        </StripeProvider>
      </NavigationContainer>
      <Toast />
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
  },
  buttonsContainer: {
    padding: 0,
  },
  textStyle: {
    textAlign: 'center',
    marginBottom: 8,
  },
});

export default App;
