import React, { useContext, useEffect, useState } from 'react';
import { SafeAreaView, SectionList, StyleSheet, Text } from 'react-native';
import Product from '../../models/productModel';
import FeaturedProducts from '../../components/products/featured-products/featured-products';
import CartProducts from '../../components/products/cart-products/cart-products';
import ProductBarCodeScanner from '../../components/products/product-barcode-scanner/product-barcode-scanner';
import { ProductContext, useProductContext } from '../../contexts/product-context';
import { colors } from '../../global-styles';


const Products: React.FC = () => {
  const [isLoadingProducts, setIsLoadingProducts] = useState<boolean>(true);
  const productContext = useProductContext();


  useEffect(() => {
    const setListOfProducts = async () => {
      let listOfProducts = await productContext.LoadProducts();
      if (listOfProducts) setIsLoadingProducts(false);
    }
    setListOfProducts();
  }, []);

  useEffect(() => {
  }, [productContext.products]);


  const sections = [
    {
      title: 'Leitor', data: [<ProductBarCodeScanner />]
    },
    {
      title: 'Cart Products', data: [<CartProducts />]
    },
    {
      title: 'Featured Products', data: [<FeaturedProducts />]
    },
  ];

  return (
    <SafeAreaView style={styles.container}>
      {isLoadingProducts &&
        <>
          <Text>is loading....</Text>
        </>
      }
      {!isLoadingProducts && productContext.products &&
        <SectionList
          style={{}}
          sections={sections}
          renderItem={({ item }) => item}
        />
      }
    </SafeAreaView>
  );
};



const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.mainAppColor,
  },
  title: {
    fontWeight: 'bold',
    fontSize: 24,
  },
});

export default Products;
