import React, { useContext } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, FlatList, Image, ScrollView } from 'react-native';
import { colors, globalStyles } from '../../../global-styles';
import { PanResponder, Animated } from 'react-native';
import Product from '../../../models/productModel';
import Ionicons from '@expo/vector-icons/Ionicons';
import { Feather } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import { ProductContext, useProductContext } from '../../../contexts/product-context';


interface ProductItemProps {
    item: Product;
}

const DraggableProductItem: React.FC<ProductItemProps> = ({ item }) => {
    const { formatToCurrency, handleRemoveFromCart } = useContext(ProductContext)

    const pan = React.useRef(new Animated.ValueXY()).current;
    const [isDeleting, setIsDeleting] = React.useState(false);

    const panResponder = React.useRef(
        PanResponder.create({
            onStartShouldSetPanResponder: (_, gestureState) => {
                return Math.abs(gestureState.dx) > 5 || Math.abs(gestureState.dy) > 5;
            },

            onMoveShouldSetPanResponder: (_, gestureState) => {
                return Math.abs(gestureState.dx) > 5 || Math.abs(gestureState.dy) > 5;
            },

            onPanResponderMove: (_, gestureState) => {
                const { dx, dy } = gestureState;

                if (Math.abs(dy) > Math.abs(dx)) {
                    return;
                }

                if (dx < -30) {
                    setIsDeleting(true);
                } else {
                    setIsDeleting(false);
                }
                Animated.event([null, { dx: pan.x }], { useNativeDriver: false })(_, gestureState);
            },

            onPanResponderRelease: (_, gestureState) => {
                if (gestureState.dx < -120) {
                    handleRemoveFromCart(item);
                } else {
                    Animated.spring(pan, {
                        toValue: { x: 0, y: 0 },
                        useNativeDriver: true,
                        friction: 5,
                    }).start();
                    setIsDeleting(false);
                }
            },
        })
    ).current;

    // onDrag Styles
    const borderColor = isDeleting ? 'red' : 'transparent';
    const backgroundColor = isDeleting ? '#484848' : colors.fiscalNoteColor;

    return (
        <ScrollView
            scrollEnabled={!isDeleting}
        >
            <Animated.View
                style={{
                    ...styles.productContainer,
                    transform: [{ translateX: pan.x }],
                    borderColor: borderColor,
                    borderWidth: 1,
                    backgroundColor: backgroundColor,
                }}
                {...panResponder.panHandlers}
            >
                <View style={styles.productContainer}>
                    <View style={styles.descriptionContainer}>
                        <Image source={{ uri: item?.url_img }} style={[styles.image]} />
                        <Text style={styles.description} numberOfLines={1} ellipsizeMode="middle">{item?.description.toUpperCase()}</Text>
                    </View>
                    <View style={styles.lineDashed} />
                    <View style={styles.priceContainer}>
                        <Text style={[globalStyles.button, { backgroundColor: 'red' }]}>
                            <Ionicons name="arrow-back" size={12} color="white" /> {''}
                            <Feather name="trash-2" size={12} color="white" />
                        </Text>
                        <Text style={isDeleting ? styles.minusPrice : styles.price}>
                            {`${isDeleting ? '-' : ''}${formatToCurrency(item?.price)}`}
                        </Text>
                    </View>
                </View>
            </Animated.View>
        </ScrollView>
    );
};

const CartProducts: React.FC = () => {
    const productContext = useProductContext();
    const navigation = useNavigation();

    return (
        <View style={styles.container}>
            <View style={globalStyles.cartHeaderContainer}>
                <Text style={styles.title}>Meu Carrinho</Text>
                <Text style={[
                    { fontWeight: 'bold' },
                    productContext.cartProducts.length < 10 ? { color: colors.secondaryColor } : { color: 'red' }
                ]}
                >
                    no carrinho: {productContext.cartProducts.length}</Text>
                <Text style={styles.simpleText}>max: 10</Text>
            </View>
            {productContext.cartProducts.length == 0 && (
                <View style={[styles.productContainer, { backgroundColor: colors.fiscalNoteColor }]}>
                    <View>
                        <Text style={{ textTransform: 'capitalize', paddingVertical: 10 }}>Sem Produtos no Carrinho</Text>
                    </View>
                </View>
            )}
            <FlatList
                data={productContext.cartProducts}
                renderItem={({ item }) => (
                    <View>
                        <DraggableProductItem item={item} />
                        {productContext.cartProducts.indexOf(item) === productContext.cartProducts.length - 1 && (
                            <View style={[styles.finalPriceContainer, styles.priceContainer]}>
                                <Text style={styles.finalPrice}>TOTAL:</Text>
                                <Text style={styles.finalPrice}>{productContext.formatToCurrency(productContext.sumProducts)}</Text>
                            </View>
                        )}
                    </View>
                )}
                keyExtractor={(item) => item.code}
                contentContainerStyle={styles.flatListContent} />
            {productContext.cartProducts.length != 0 && (
                <View>
                    <View style={styles.containerFinalizar}>
                        <TouchableOpacity
                            style={[styles.buttonFinalizar]}
                            onPress={
                                () => {
                                    productContext.setIsScannerAlive(false);
                                    navigation.navigate('Payment');
                                }}>
                            <Text style={[styles.buttonFinalizarText]}>Finalizar</Text>
                        </TouchableOpacity>
                    </View>
                </View>
            )}
            <View style={styles.lineSolid}></View>
        </View>
    );
};


const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 10,
        height: 'auto',
    },
    productContainer: {
        flexDirection: 'column',
        marginVertical: 1,
        paddingHorizontal: 2,
        paddingBottom: 2,
    },
    finalPriceContainer: {
        marginTop: 1,
        marginVertical: 1,
        padding: 6,
        backgroundColor: colors.fiscalNoteColor,
    },
    priceContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        paddingVertical: 4,
    },
    descriptionContainer: {
        flexDirection: 'row',
        alignItems: 'center',
    },
    containerFinalizar: {
        justifyContent: 'center',
        alignItems: 'center',
        marginVertical: 10,
    },
    title: {
        fontWeight: '500',
        fontSize: 18,
        color: colors.title,
        paddingVertical: 4,
    },
    simpleText: {
        color: 'white',
        fontWeight: 'bold'
    },
    lineDashed: {
        borderBottomColor: 'black',
        borderBottomWidth: 1,
        borderStyle: 'dashed',
        width: '100%',
    },
    lineSolid: {
        borderBottomColor: '#7F8A7D',
        borderBottomWidth: 1,
        borderStyle: 'solid',
        width: '100%',
        marginTop: 6,
    },
    image: {
        width: 40,
        height: 40,
        resizeMode: 'contain',
        marginBottom: 8,
        marginRight: 2,
    },
    description: {
        fontSize: 12,
        marginBottom: 4,
        textTransform: 'uppercase',
        fontWeight: '500',
    },
    price: {
        fontSize: 14,
        fontWeight: 'bold',
        color: 'green',
    },
    minusPrice: {
        fontSize: 14,
        fontWeight: 'bold',
        color: 'white',
    },
    finalPrice: {
        borderTopColor: 'white',
        fontWeight: 'bold',
        paddingVertical: 8,
        textTransform: 'uppercase',
    },
    messageError: {
        marginLeft: 14,
        fontWeight: '500',
        fontSize: 18,
        color: 'red',
    },
    cartContainer: {
        flex: 1,
        marginTop: 20,
    },
    cartTitle: {
        fontSize: 20,
        fontWeight: 'bold',
        marginBottom: 10,
        textAlign: 'center',
    },
    flatListContent: {
        justifyContent: 'center',
    },
    buttonFinalizar: {
        backgroundColor: colors.secondaryColor,
        borderRadius: 8,
        padding: 12,
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.3,
        shadowRadius: 2,
        elevation: 5,
    },
    buttonFinalizarText: {
        color: 'white',
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: 16,
    },
});

export default CartProducts;
