import * as React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { StyleSheet, Text } from 'react-native';
import type { StatusBarStyle } from 'react-native';
import { StatusBar as ExpoStatusBar } from 'expo-status-bar';
import Toast from 'react-native-toast-message'

import AuthRouter from './src/app/routes/Auth/auth-router';
import ProductProvider from './src/app/contexts/product-context';
import { useAuth } from './src/app/contexts/auth-context';
import NonAuthRouter from './src/app/routes/nonAuth/non-auth-router';


const TRANSITIONS = ['fade', 'slide', 'none'] as const;
const STYLES = ['default', 'dark-content', 'light-content'] as const;

function App() {
  const [statusBarStyle, setStatusBarStyle] = React.useState<StatusBarStyle>(
    STYLES[2],
  );

  const { isLogged } = useAuth();

  return (
    <>
      <NavigationContainer>
        <ExpoStatusBar
          style={statusBarStyle === 'default' ? 'light' : 'light'}
          backgroundColor='black' />
        <ProductProvider>
          {/* { isLogged ? <AuthRouter /> : <NonAuthRouter /> } */}
          <AuthRouter />
        </ProductProvider>
      </NavigationContainer>
      <Toast />
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
  },
  buttonsContainer: {
    padding: 0,
  },
  textStyle: {
    textAlign: 'center',
    marginBottom: 8,
  },
});

export default App;
