import React, { useEffect, useState } from 'react';
import { StyleSheet, View } from 'react-native';
import { Input, Icon, Text, Button } from '@rneui/themed';
import { SafeAreaView } from 'react-native-safe-area-context';
import { User } from '../../types/user';
import { BASE_URL_API } from '../../../../config';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { useNavigation } from '@react-navigation/native';
import { useAuth } from '../../contexts/auth-context';

export default function SignUp() {
    const navigation = useNavigation();
    const auth = useAuth();

    const [formData, setFormData] = useState({
        name: '',
        username: '',
        password: '',
        cpf: '',
    });

    const validateEmail = (email: string) => {
        return String(email)
            .toLowerCase()
            .match(
                /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|.(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
            );
    };

    const signUp = async (): Promise<any> => {
        console.log(formData);

        try {
            const response = await fetch('http://localhost:3000/auth/signup', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    "name": "chris",
                    "username": "chris2@gmail.com",
                    "password": "string",
                    "cpf": "123454"
                }),
            });

            console.error(response.status)

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const responseData = await response.json();

            console.log(responseData);

            if (responseData.access_token) {
                await AsyncStorage.setItem('TOKEN', responseData.access_token);
                auth.setSignedIn(true);
                return responseData;
            } else {
                throw new Error('Access token not found in response');
            }
        } catch (error) {
            console.error('Error during sign up:', error);
            throw new Error('Sign up failed');
        }
    };


    const updateField = (fieldName: string, value: any) => {
        setFormData(prevData => ({
            ...prevData,
            [fieldName]: value,
        }));
    };

    return (
        <SafeAreaView>
            <View style={styles.loginForm}>
                <Text h4 h4Style={{ color: 'gray', alignSelf: 'flex-start', marginLeft: 10, marginBottom: 15 }}>Novo Usuário</Text>
                <Input
                    style={styles.input}
                    placeholder='Seu Nome'
                    errorStyle={{ color: 'red' }}
                    errorMessage={""}
                    onChangeText={newText => updateField('name', newText)}
                />
                <Input
                    style={styles.input}
                    placeholder='Seu Email'
                    errorStyle={{ color: 'red' }}
                    errorMessage={""}
                    onChangeText={newText => updateField('username', newText)}
                />
                <Input
                    style={styles.input}
                    placeholder="Senha"
                    errorStyle={{ color: 'red' }}
                    errorMessage={""}
                    secureTextEntry={true}
                    onChangeText={newText => updateField('password', newText)}
                />
                <Input
                    style={styles.input}
                    placeholder='Seu CPF'
                    errorStyle={{ color: 'red' }}
                    errorMessage={""}
                    onChangeText={newText => updateField('cpf', newText)}
                    keyboardType='numeric'
                />
                <Button
                    title="Cadastrar-se"
                    loading={false}
                    loadingProps={{ size: 'small', color: 'white' }}
                    buttonStyle={{
                        backgroundColor: 'rgba(111, 202, 186, 1)',
                        borderRadius: 5,
                    }}
                    titleStyle={{ fontWeight: 'bold' }}
                    containerStyle={{
                        height: 50,
                        width: 200,
                    }}
                    onPress={() => {
                        signUp()
                            .then(user => {
                                console.log('Sign up successful:', user);
                            })
                            .catch(error => {
                                console.error('Sign up failed:', error);
                            })
                    }}
                />
            </View>
        </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    loginForm: {
        height: '100%',
        justifyContent: 'center',
        alignItems: 'center',
    },
    input: {
        width: '80%',
    },
})
