import React, { useContext, useEffect, useState } from 'react';
import { View, Text, Image, StyleSheet, TouchableOpacity, FlatList } from 'react-native';
import { colors, globalStyles } from '../../../global-styles';
import Product from '../../../models/productModel';
import Ionicons from '@expo/vector-icons/Ionicons';
import { FontAwesome } from '@expo/vector-icons';
import { useProductContext } from '../../../contexts/product-context';


interface ProductItemProps {
    item: Product;
}

const ProductItem: React.FC<ProductItemProps> = ({ item }) => {
    const productContext = useProductContext();

    return (
        <View style={styles.productContainer}>
            <Image
                source={{ uri: item?.img }}
                style={styles.image}
            />
            <Text style={styles.description} numberOfLines={2} ellipsizeMode="middle">{item?.description.toUpperCase()}</Text>
            <View style={styles.priceContainer}>
                <Text style={styles.price}>{productContext.formatToCurrency(item?.price)}</Text>

                {productContext.cartProducts.length < 9 ? (
                    <TouchableOpacity
                        style={[globalStyles.button, styles.addToCart]}
                        onPress={() => productContext.handleAddProductToCart(item)}
                    >
                        <FontAwesome name="cart-arrow-down" size={16} color="white" />
                    </TouchableOpacity>
                ) : <Text style={[globalStyles.button, styles.fullCart]}>
                    <Ionicons name="md-cart-outline" size={14} color="white" /> Cheio</Text>}
            </View>
        </View>
    );
};

const FeaturedProducts: React.FC = () => {
    // insert into product(code, description, url_img, unit_price) values ("12345671","BATATA CONG NOISETTE SADIA PC400GR","https://storage.googleapis.com/phygital_files/mercado-milenio/uploads/produto/batata_cong_noisette_sadia_pc400gr_bc899539-59c7-400c-8f7c-2f2fa085eb7a.png",23.34),("12345672","SABAO DE COCO BARRA 100G","https://storage.googleapis.com/phygital_files/comercial-bambina/uploads/produto/sabao_de_coco_barra_100g_5f4276ff-fbcf-4c17-a080-c3807b6a8991.png", 3.75),("12345673","Macarrão instantâneo luccas neto - sabor carne","https://images-americanas.b2w.io/produtos/3398641894/imagens/macarrao-instantaneo-luccas-neto-sabor-carne-sandella/3398641894_1_large.jpg",4.99),("12345674","MUCILON ARROZ E AVEIA SH360GR","https://storage.googleapis.com/phygital_files/mercado-milenio/uploads/produto/mucilon_arroz_e_aveia_sh360gr_c1ebc0e9-fec9-43cb-b180-7c6be0a8998d.png",13.99);
    const productContext = useProductContext();
    const [isLoadingProducts, setIsLoadingProducts] = useState<boolean>(true);

    useEffect (() => {
        const setListOfProducts = async () => {
            let listOfProducts = await productContext.LoadProducts();
            if(listOfProducts) setIsLoadingProducts(false);
        } 
        setListOfProducts();
    }, []);

    return (
        <View style={styles.container}>
            {isLoadingProducts && (
                <Text style={{ color: 'white'}}>is loading</Text>
            )}
            {!isLoadingProducts && productContext.products && (
                <>
                    <Text style={[styles.title]}>Produtos em Destaque</Text>
                    <View style={styles.container}>
                        <FlatList
                            data={productContext.products}
                            renderItem={({ item }) => (
                                <ProductItem item={item} />
                            )}
                            keyExtractor={(item) => item.code}
                            contentContainerStyle={null}
                            numColumns={2}
                        />
                    </View>
                </>
            )}
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingTop: 6,
        height: 'auto',
        paddingHorizontal: 6,
        paddingBottom: 6,
    },
    image: {
        height: 140,
        resizeMode: 'contain',
        borderTopRightRadius: 12,
        borderTopLeftRadius: 12,
        backgroundColor: 'white',
    },
    productContainer: {
        flex: 1,
        flexDirection: 'column',
        backgroundColor: colors.productFooter,
        margin: 3,
        paddingBottom: 6,
        borderRadius: 12,
        borderWidth: .4,
        borderColor: 'black',
    },
    containerReload: {
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: 16,
    },
    title: {
        fontWeight: '500',
        fontSize: 18,
        color: colors.title,
        paddingLeft: 4,
    },
    description: {
        fontSize: 11,
        textAlign: 'center',
        paddingHorizontal: 2,
        fontWeight: '500',
    },
    priceContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        paddingHorizontal: 10,
        paddingVertical: 4,
    },
    price: {
        fontSize: 14,
        fontWeight: 'bold',
    },
    disabledButton: {
        backgroundColor: 'black',
    },
    messageError: {
        marginLeft: 14,
        fontWeight: '500',
        fontSize: 18,
        color: 'red',
    },
    addToCart: {
        backgroundColor: '#7D0E7C',
        paddingHorizontal: 12,
        paddingVertical: 6
    },
    fullCart: {
        backgroundColor: '#7D0E7C',
        color: 'white',
    }
});

export default FeaturedProducts;
