import React, { useContext, useEffect, useState } from 'react';
import { SectionList, SafeAreaView, StyleSheet, Text } from 'react-native';
import FeaturedProducts from '../../components/products/featured-products/featured-products';
import { colors } from '../../global-styles';
import { useProductContext } from '../../contexts/product-context';
import Product from '../../models/productModel';


function Home() {

  const productContext = useProductContext();

  const [products, setProducts] = useState<Product[] | undefined>(undefined);
  const [isLoadingProducts, setIsLoadingProducts] = useState<boolean>(true);

  const LoadProducts = async (): Promise<Product[] | undefined> => {
    try {
      const response = await fetch("http://localhost:3000/product");
      const data = await response.json();
      console.error(data);
      setProducts(data);
      return data;
    } catch (error) {
      console.error("Error fetching products:", error);
      setProducts(undefined);
      return undefined
    }
  };

  useEffect(() => {
    let x = async () => {
      let temp = await LoadProducts()
      if (temp) {
        setProducts(temp);
        setIsLoadingProducts(false);
      }
    }

    x();
  }, [])

  const sections = [
    {
      title: 'Featured Products', data: [<FeaturedProducts products={products} />]
    },
  ];

  return (
    <SafeAreaView style={styles.container}>
      {!isLoadingProducts ? (
        <SectionList
          style={{}}
          sections={sections}
          renderItem={({ item }) => item}
        />
      ) :
        <Text>ertertertert</Text>
      }
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    backgroundColor: colors.mainAppColor,
  },
});

export default Home;
