import React, { useContext, useEffect, useState } from 'react';
import { View, Text, Image, StyleSheet, TouchableOpacity, FlatList } from 'react-native';
import { colors, globalStyles } from '../../../global-styles';
import Product from '../../../models/productModel';
import Ionicons from '@expo/vector-icons/Ionicons';
import { FontAwesome } from '@expo/vector-icons';
import { useProductContext } from '../../../contexts/product-context';


interface ProductItemProps {
    item: Product;
}

const ProductItem: React.FC<ProductItemProps> = ({ item }) => {
    const productContext = useProductContext();

    return (
        <View style={styles.productContainer}>
            <Image
                source={{ uri: item?.url_img }}
                style={styles.image}
            />
            <Text style={styles.description} numberOfLines={2} ellipsizeMode="middle">{item?.description.toUpperCase()}</Text>
            <View style={styles.priceContainer}>
                <Text style={styles.price}>{productContext.formatToCurrency(item?.unit_price)}</Text>

                {productContext.cartProducts.length < 9 ? (
                    <TouchableOpacity
                        style={[globalStyles.button, styles.addToCart]}
                        onPress={() => productContext.handleAddProductToCart(item)}
                    >
                        <FontAwesome name="cart-arrow-down" size={16} color="white" />
                    </TouchableOpacity>
                ) : <Text style={[globalStyles.button, styles.fullCart]}>
                    <Ionicons name="md-cart-outline" size={14} color="white" /> Cheio</Text>}
            </View>
        </View>
    );
};

const FeaturedProducts: React.FC = () => {
    const productContext = useProductContext();

    const [isLoadingProducts, setIsLoadingProducts] = useState<boolean>(true);

    useEffect(() => {
        const getProducts = async () => {
            try {
                const fetchedProducts = await productContext.LoadProducts();
                productContext.setProducts(fetchedProducts);
            } catch (error) {
                console.error('Error fetching products:', error);
            } finally {
                setIsLoadingProducts(false);
            }
        };

        getProducts();
    }, []);

    useEffect(()=>{

    },[productContext.products])

    return (
        <View style={styles.container}>
            <Text style={styles.title}>Produtos em Destaque</Text>
            <View style={styles.container}>
                {!isLoadingProducts && (
                    <FlatList
                        data={productContext.products}
                        renderItem={({ item }) => (
                            <ProductItem item={item} />
                        )}
                        keyExtractor={(item) => item.code}
                        contentContainerStyle={null}
                        numColumns={2}
                    />
                )}
                {isLoadingProducts && (
                    <Text>Loading...</Text>
                )}
            </View>
        </View>
    );
};


const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingTop: 6,
        height: 'auto',
        paddingHorizontal: 6,
        paddingBottom: 6,
    },
    image: {
        height: 140,
        resizeMode: 'contain',
        borderTopRightRadius: 12,
        borderTopLeftRadius: 12,
        backgroundColor: 'white',
    },
    productContainer: {
        flex: 1,
        flexDirection: 'column',
        backgroundColor: colors.productFooter,
        margin: 3,
        paddingBottom: 6,
        borderRadius: 12,
        borderWidth: .4,
        borderColor: 'black',
    },
    containerReload: {
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: 16,
    },
    title: {
        fontWeight: '500',
        fontSize: 18,
        color: colors.title,
        paddingLeft: 4,
    },
    description: {
        fontSize: 11,
        textAlign: 'center',
        paddingHorizontal: 2,
        fontWeight: '500',
    },
    priceContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        paddingHorizontal: 10,
        paddingVertical: 4,
    },
    price: {
        fontSize: 14,
        fontWeight: 'bold',
    },
    disabledButton: {
        backgroundColor: 'black',
    },
    messageError: {
        marginLeft: 14,
        fontWeight: '500',
        fontSize: 18,
        color: 'red',
    },
    addToCart: {
        backgroundColor: '#7D0E7C',
        paddingHorizontal: 12,
        paddingVertical: 6
    },
    fullCart: {
        backgroundColor: '#7D0E7C',
        color: 'white',
    }
});

export default FeaturedProducts;
